import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  // State set for user's input change
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  // set function to get user's input
  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    // Change the state
    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    // This connects to NewExpense function in NewExpense.js with the above object expenseData
    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  };
  // Logic to add a cancel expense form button
  const [changeText, setChangeText] = useState(true);

  const handleChange = () => {
    return setChangeText(!changeText);
  };

  const cancelHandle = () => {
    return setChangeText(changeText);
  };
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      // pull key value pairs from expenseData in ExpenseForm
      ...enteredExpenseData,
      // set a random ID
      id: Math.random().toString(),
    };
    // allows the result to be seen in app.js
    props.onAddExpense(expenseData);
  };
  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          {/* Added listener for users input */}
          <input
            type="text"
            // Gather user input then change it
            value={enteredTitle}
            onChange={titleChangeHandler}
          ></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            value={enteredAmount}
            onChange={amountChangeHandler}
          ></input>
        </div>
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            value={enteredDate}
            onChange={dateChangeHandler}
          ></input>
        </div>
      </div>
      <div className="new-expense__actions">
        <button onClick={() => handleChange()}>Add Expense</button>
        {changeText ? (
          ""
        ) : (
          <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
        )}
        <button type="submit">Add expenses</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
