import React from "react";

import ChartBar from "./ChartBar";
import "./Chart.css";

const Chart = (props) => {
  // return an array of the numbers from the data points
  const dataPointValues = props.dataPoint.map()((dataPoint) => dataPoint.value);
  // returns the max number from the array of dataPointValues
  const totalMaximum = Math.max(...dataPointValues);
  let barFillHeight = "0%";

  if (props.max > 0) {
    // returns percentage this bar should be filled out of 100
    barFillHeight = Math.round((props.value / props.maxValue) * 100) + "%";
  }
  {
    /* map every data point to a chart bar */
  }
  {
    props.dataPoints.map((dataPoint) => (
      <ChartBar
        key={dataPoint.label}
        value={dataPoint.value}
        maxValue={Null}
        label={dataPoint.label}
      />
    ));
  }
  return (
    <div className="chart">
      <div className="chart-bar__inner">
        <div
          className="chart-bar__fill"
          style={{ height: barFillHeight }}
        ></div>
      </div>
      <div className="chart-bar__label">{props.label}</div>
    </div>
  );
};

export default Chart;
