import React, { useState } from "react";
import NewExpense from "./components/NewExpense/NewExpense";
import Expenses from "./components/Expenses/Expenses";

const App = () => {
  // Set the current state and the function used to update the state
  const [expenses, setExpenses] = useState("");

  const addExpenseHandler = (expense) => {
    // automatically receive latest state snapshot
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };

  return (
    <div>
      {/* Gets users input, returns user's expense data */}
      <NewExpense onAddExpense={addExpenseHandler} />
      {/* Displays expense data in a list contained in a card */}
      {/* items are an array of all your ExpenseItem in Expenses.js */}
      <Expenses items={expenses} />
    </div>
  );
};

export default App;
